/*global module */
var getTotal = function (args) {
    if (!Array.isArray(args)) {
        throw new Error("The argument must be of type array.");
    }
    var result = 0;
    for (var i = 0, len = args.length; i < len; i++) {
        result += args[i];
    }
    return result;
};

module.exports = getTotal;
